mapper.select:
select id,name, server_ip, status, group_id, create_time
from t_robot
where id = #{id}
  and delete_flg = 0;

select tr.id,
       tr.name,
       tr.server_ip,
       tr.status,
       tr.group_id,
       tr.create_time,
       tg.name as groupName,
       tt.name as taskName
from t_robot tr
         left join t_group tg on tr.group_id = tg.id and tg.delete_flg = 0
         left join t_task tt on tr.cur_task_id = tt.id and tt.delete_flg = 0
where tr.delete_flg = 0


    and tr.name = #{name}


    and tr.id = #{id}


    and tr.cur_task_id = #{curTaskId}


    and tr.group_id = #{groupId}


    and tr.status = #{status}


    and tr.server_ip = #{serverIp}


order by create_time desc
